mapper.insert:
insert into Device (owner_id, name, type
  )
values (#{ownerId,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{deviceType,jdbcType=TINYINT}
  )

mapper.update:
update Device
set owner_id = #{ownerId,jdbcType=INTEGER},
  name = #{name,jdbcType=VARCHAR},
  type = #{deviceType,jdbcType=TINYINT}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, owner_id, name, type
from Device
where id = #{id,jdbcType=INTEGER}
